        if (ameriResistor) {
            //// Bezier 2 Drawing
            let bezier2Path = NSBezierPath()
            bezier2Path.move(to: NSPoint(x: 3, y: 23))
            bezier2Path.curve(to: NSPoint(x: 27, y: 23), controlPoint1: NSPoint(x: 27, y: 23), controlPoint2: NSPoint(x: 27, y: 23))
            bezier2Path.line(to: NSPoint(x: 38, y: 39))
            bezier2Path.line(to: NSPoint(x: 55, y: 7))
            bezier2Path.line(to: NSPoint(x: 71, y: 39))
            bezier2Path.line(to: NSPoint(x: 87, y: 7))
            bezier2Path.line(to: NSPoint(x: 104, y: 39))
            bezier2Path.curve(to: NSPoint(x: 120, y: 7), controlPoint1: NSPoint(x: 104, y: 39), controlPoint2: NSPoint(x: 120, y: 5))
            bezier2Path.curve(to: NSPoint(x: 128, y: 23), controlPoint1: NSPoint(x: 120, y: 9), controlPoint2: NSPoint(x: 128, y: 23))
            bezier2Path.line(to: NSPoint(x: 152, y: 23))
            NSColor.red.setStroke()
            bezier2Path.lineWidth = 5
            bezier2Path.lineCapStyle = .roundLineCapStyle
            bezier2Path.lineJoinStyle = .roundLineJoinStyle
            bezier2Path.stroke()
        }


        if (euroResistor) {
            //// Bezier 3 Drawing
            let bezier3Path = NSBezierPath()
            bezier3Path.move(to: NSPoint(x: 25.08, y: 7.5))
            bezier3Path.line(to: NSPoint(x: 128.89, y: 7.5))
            bezier3Path.line(to: NSPoint(x: 128.89, y: 38.5))
            bezier3Path.line(to: NSPoint(x: 25.08, y: 38.5))
            bezier3Path.line(to: NSPoint(x: 25.08, y: 7.5))
            bezier3Path.close()
            bezier3Path.move(to: NSPoint(x: 129.92, y: 22.53))
            bezier3Path.line(to: NSPoint(x: 151.5, y: 22.53))
            bezier3Path.move(to: NSPoint(x: 3.5, y: 22.53))
            bezier3Path.line(to: NSPoint(x: 25.08, y: 22.53))
            NSColor.white.setFill()
            bezier3Path.fill()
            NSGraphicsContext.saveGraphicsState()
            context.setShadow(offset: NSSize(width: shadow2.shadowOffset.width * resizedShadowScale, height: shadow2.shadowOffset.height * resizedShadowScale), blur: shadow2.shadowBlurRadius * resizedShadowScale, color: shadow2.shadowColor!.cgColor)
            NSColor.red.setStroke()
            bezier3Path.lineWidth = 5
            bezier3Path.lineCapStyle = .roundLineCapStyle
            bezier3Path.stroke()
            NSGraphicsContext.restoreGraphicsState()
        }
        
        NSGraphicsContext.restoreGraphicsState()

    }

    @objc dynamic public class func drawGradientButton(frame targetFrame: NSRect = NSRect(x: 0, y: 0, width: 131, height: 40), resizing: ResizingBehavior = .aspectFit, selectedGradientColor: NSColor = NSColor(red: 0.288, green: 0.923, blue: 0.352, alpha: 1)) {
        //// General Declarations
        let context = NSGraphicsContext.current!.cgContext
        
        //// Resize to Target Frame
        NSGraphicsContext.saveGraphicsState()
        let resizedFrame: NSRect = resizing.apply(rect: NSRect(x: 0, y: 0, width: 131, height: 40), target: targetFrame)
        context.translateBy(x: resizedFrame.minX, y: resizedFrame.minY)
        context.scaleBy(x: resizedFrame.width / 131, y: resizedFrame.height / 40)



        //// Gradient Declarations
        let selectedGradient = NSGradient(starting: NSColor.white, ending: selectedGradientColor)!

        //// Rectangle Drawing
        let rectanglePath = NSBezierPath(roundedRect: NSRect(x: 3, y: 3, width: 126, height: 35), xRadius: 4, yRadius: 4)
        selectedGradient.draw(in: rectanglePath, angle: -90)
        NSColor.gray.setStroke()
        rectanglePath.lineWidth = 2
        rectanglePath.stroke()
        
        NSGraphicsContext.restoreGraphicsState()

    }

