        shadow2.shadowOffset = CGSize(width: 1, height: 1)
        shadow2.shadowBlurRadius = 4

        //// Variable Declarations
        // let euroResistor = false
        let ameriResistor = !euroResistor

        //// Group
        if (ameriResistor) {
            //// Bezier 2 Drawing
            let bezier2Path = UIBezierPath()
            bezier2Path.move(to: CGPoint(x: 3, y: 23))
            bezier2Path.addCurve(to: CGPoint(x: 27, y: 23), controlPoint1: CGPoint(x: 27, y: 23), controlPoint2: CGPoint(x: 27, y: 23))
            bezier2Path.addLine(to: CGPoint(x: 38, y: 7))
            bezier2Path.addLine(to: CGPoint(x: 55, y: 39))
            bezier2Path.addLine(to: CGPoint(x: 71, y: 7))
            bezier2Path.addLine(to: CGPoint(x: 87, y: 39))
            bezier2Path.addLine(to: CGPoint(x: 104, y: 7))
            bezier2Path.addCurve(to: CGPoint(x: 120, y: 39), controlPoint1: CGPoint(x: 104, y: 7), controlPoint2: CGPoint(x: 120, y: 41))
            bezier2Path.addCurve(to: CGPoint(x: 128, y: 23), controlPoint1: CGPoint(x: 120, y: 37), controlPoint2: CGPoint(x: 128, y: 23))
            bezier2Path.addLine(to: CGPoint(x: 152, y: 23))
            UIColor.red.setStroke()
            bezier2Path.lineWidth = 5
            bezier2Path.lineCapStyle = .round
            bezier2Path.lineJoinStyle = .round
            bezier2Path.stroke()
        }


        if (euroResistor) {
            //// Bezier 3 Drawing
            let bezier3Path = UIBezierPath()
            bezier3Path.move(to: CGPoint(x: 25.08, y: 38.5))
            bezier3Path.addLine(to: CGPoint(x: 128.89, y: 38.5))
            bezier3Path.addLine(to: CGPoint(x: 128.89, y: 7.5))
            bezier3Path.addLine(to: CGPoint(x: 25.08, y: 7.5))
            bezier3Path.addLine(to: CGPoint(x: 25.08, y: 38.5))
            bezier3Path.close()
            bezier3Path.move(to: CGPoint(x: 129.92, y: 23.47))
            bezier3Path.addLine(to: CGPoint(x: 151.5, y: 23.47))
            bezier3Path.move(to: CGPoint(x: 3.5, y: 23.47))
            bezier3Path.addLine(to: CGPoint(x: 25.08, y: 23.47))
            UIColor.white.setFill()
            bezier3Path.fill()
            context.saveGState()
            context.setShadow(offset: CGSize(width: shadow2.shadowOffset.width * resizedShadowScale, height: shadow2.shadowOffset.height * resizedShadowScale), blur: shadow2.shadowBlurRadius * resizedShadowScale, color: (shadow2.shadowColor as! UIColor).cgColor)
            UIColor.red.setStroke()
            bezier3Path.lineWidth = 5
            bezier3Path.lineCapStyle = .round
            bezier3Path.stroke()
            context.restoreGState()
        }
        
        context.restoreGState()

    }

    @objc dynamic public class func drawGradientButton(frame targetFrame: CGRect = CGRect(x: 0, y: 0, width: 131, height: 40), resizing: ResizingBehavior = .aspectFit, selectedGradientColor: UIColor = UIColor(red: 0.288, green: 0.923, blue: 0.352, alpha: 1.000), label: String = "Maraschino") {
        //// General Declarations
        let context = UIGraphicsGetCurrentContext()!
        
        //// Resize to Target Frame
        context.saveGState()
        let resizedFrame: CGRect = resizing.apply(rect: CGRect(x: 0, y: 0, width: 131, height: 40), target: targetFrame)
        context.translateBy(x: resizedFrame.minX, y: resizedFrame.minY)
        context.scaleBy(x: resizedFrame.width / 131, y: resizedFrame.height / 40)



        //// Gradient Declarations
        let selectedGradient = CGGradient(colorsSpace: nil, colors: [UIColor.white.cgColor, selectedGradientColor.cgColor] as CFArray, locations: [0, 1])!

        //// Rectangle Drawing
        let rectanglePath = UIBezierPath(roundedRect: CGRect(x: 3, y: 2, width: 126, height: 35), cornerRadius: 4)
        context.saveGState()
        rectanglePath.addClip()
        context.drawLinearGradient(selectedGradient, start: CGPoint(x: 66, y: 2), end: CGPoint(x: 66, y: 37), options: [])
        context.restoreGState()
        UIColor.gray.setStroke()
        rectanglePath.lineWidth = 2
        rectanglePath.stroke()


        //// Text Drawing
        let textRect = CGRect(x: 17, y: 7, width: 99, height: 24)
        let textStyle = NSMutableParagraphStyle()
        textStyle.alignment = .center
        let textFontAttributes = [
            .font: UIFont(name: "HelveticaNeue", size: 17)!,
            .foregroundColor: UIColor.black,
            .paragraphStyle: textStyle,
        ] as [NSAttributedStringKey: Any]

        let textTextHeight: CGFloat = label.boundingRect(with: CGSize(width: textRect.width, height: CGFloat.infinity), options: .usesLineFragmentOrigin, attributes: textFontAttributes, context: nil).height
        context.saveGState()
        context.clip(to: textRect)
        label.draw(in: CGRect(x: textRect.minX, y: textRect.minY + (textRect.height - textTextHeight) / 2, width: textRect.width, height: textTextHeight), withAttributes: textFontAttributes)
        context.restoreGState()
        
        context.restoreGState()

    }

